
/// SELECT COMPONENT
function selectComponent( name, classStyle, valueInDB, values ={} ){
   let element = `<select name="${ name }" class="${classStyle}">`
   values.forEach( function(item){
      element += `<option value="${ item.value }" ${ valueInDB  == item.value ? "selected" : "" }>${ item.text }</option>`
   })
   element += `</select>`
   return element
}

